import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import bamboo  from './bamboo.png';
import quokka  from './Quokka.png';
import { Slide } from 'react-slideshow-image';
import 'react-slideshow-image/dist/styles.css';

export default function Projects() {

    const Pouch = {
        title: 'Pouch Loyalty',
        description: 'Pouch is a React Native mobile application designed to store loyalty points on mobile. Pouch\'s uses Firebase as its backend. Pouch started as a fun personal project. However, as Pouch is approaching completion it will be published on App Store.',
        key_reflection: `
            Through Pouch I have learned a lot of lesson. I started Pouch back in July 2021 with zero knowledge of React Native and some background in React. 
        `
    }

    return <>
        <div className="projects-container">
            <div className='project-header'>
                <h1 className="header-project">Projects</h1>
            </div>
            <Link to='/IndividualProject' state={Pouch} >
                <div className="box" id='quokka-box'>
                    <div className='img-box'>
                        <img id='quokka' src={quokka}></img>
                    </div>
                    <div className='txt-box'>
                        <h1 className='prj-title'>Pouch</h1>
                        <p>A React Native app made to store loyalty points on mobile phone.</p>
                        <div className='language-cont'>
                            <div className='language-box'>React Native</div>
                            <div className='language-box'>Firebase</div>
                            <div className='language-box'>React</div>
                            <div className='language-box'>HTML</div>
                            <div className='language-box'>CSS</div>
                            <div className='language-box'>JS</div>
                        </div>
                    </div>
                </div>
            </Link>
            <Link className="box" id='auth-box' to='/'>
                <div className='txt-box' id='prj-two'>
                    <h1 className='prj-title'>Web App Authentication</h1>
                    <p>Constructed a login and a sign up page. Design the flow of user authentication, email verification, password reset, and integrated with AWS Cognito.</p>
                    <div className='language-cont'>
                        <div className='language-box'>React</div>
                        <div className='language-box'>Next.js</div>
                        <div className='language-box'>AWS Cognito</div>
                        <div className='language-box'>HTML</div>
                        <div className='language-box'>CSS</div>
                        <div className='language-box'>JS</div>
                    </div>
                </div>
                <div className='img-box'>
                </div>
            </Link>

            <div className="box" style={{backgroundColor: '#FFCC66'}} onClick={() => console.log('here')}>
                <div className='img-box'>
                    <img id='bamboo' src={bamboo}></img>

                </div>
                <div className='txt-box'>
                    <h1 className='prj-title'>Bamboo</h1>
                    <p>A productivity tracker for making to-do list, earn points upon completing tasks, add friends, view friends tasks and send challenges.</p>
                    <div className='language-cont'>
                        <div className='language-box'>React</div>
                        <div className='language-box'>Auth0</div>
                        <div className='language-box'>HTML</div>
                        <div className='language-box'>CSS</div>
                        <div className='language-box'>JS</div>
                        <div className='language-box'>Hackiethon 2021</div>

                    </div>
                </div>
            </div>                
        </div>
        <style jsx>{`
        a {
            margin: 0;
            text-decoration: none;
        }
        .projects-container {
            display: flex;
            flex-direction: column;
            justify-content: center;
            align-items: center;
            padding: 100px;
            padding-top: 30px;
        }
        .header-project {
            font-size: 48px;
            font-weight: 600;            
        }
        .box {
            height: 250px;
            width: 100%;
            border: 1px solid black;
            display: flex;
            flex-direction: row;
            overflow: hidden;
            margin-top: 20px;
        }

        .box:hover {
            cursor: pointer;
            transform: translateY(-5px);
        }

        .prjImg {
            object-fit: cover;
            height: 100%;
        }

        #prj-caption {
            height: 15%;
            width: 100%;
            color: grey;
            font-size: 14px;
            font-weight: 500;
        }

        #prj-des {
            height: 30%;
            width: 100%;
            font-size: 14px;
            font-weight: 500;
        }

        #git-button {
            height: 40px;
            width: 150px;
            background-color: #ed755a;
            border-radius: 10px;
            display: flex;
            align-items: center;
            padding-left: 10px;
            padding-right: 20px;
            justify-content: space-between;
            margin-top: 10px;
            flex-direction: row;
        }
        #quokka {
            height: auto;
            width: 100%;
            margin-left: 90px;
        }

        #bamboo {
            width: 80%;
            height: auto;
        }

        #quokka-box {
            background-color: #FF6A6B;
        }

        #auth-box {
            background-color: #151619;
            color: white;
        }

        .img-box {
            flex: 1;
        }

        .txt-box {
            flex: 3;
            display: flex;
            justify-content: flex-end;
            align-items: flex-end;
            padding: 50px;
            flex-direction: column;
        }
        #prj-two {
            justify-content: flex-start;
            align-items: flex-start;  
            flex: 2;
        }
        .language-box {
            display: flex;
            height: 40px;
            border: 1px solid white;
            border-radius: 10px;
            padding: 2px;
            padding-left: 10px;
            padding-right: 10px;
            justify-content: center;
            align-items: center;
            margin-right: 7px;
            color: white;
        }

        .language-box:hover {
            border: 1px solid #FFC0CB;
            color: #FFC0CB;
            transition: all 0.2s linear;
        }

        .language-cont {
            display: flex;
            flex-wrap: wrap;
            margin-top: 20px;
        }
        .prj-title {
            font-size: 44px;
            font-weight: 600;
            color: white;
        }
        .project-header {
            width: 100%;
        }
        `}
        </style>
    </>
}

export function IndividualProject(props) {

    const slideImages = [
        {
            url: 'quokka.png',
            caption: 'Slide 1'
        },
        {
          url: 'quokka.png',
          caption: 'Slide 2'
        },
        {
            url: 'quokka.png',
            caption: 'Slide 3'
        },
      ];

    return <>
        <div className='project-container'>
            <div className='project-box'>
                <div className='img-box'>

                </div>
                <div className='description'>

                </div>


            </div>
        </div>
        <style jsx>{`
            .project-container {
                width: 100vw;
                display: flex;
                flex-direction: column;
                justify-content: center;
                align-items: center;
            }
            .project-box {
                width: 80%;
                height: 100%;
                border: 1px solid black;
                margin-top: 20px;
            }

            .img-box {
                height: 200px;
                width: 100%;
                border: 1px solid black;

            }


        `}</style>
    </>
}
