import React from 'react';
import { AiOutlineFileDone } from "react-icons/ai";
import cv from './cv.pdf';


export default function AboutMe() {

    return <>
        <div id="about-container">
            <div id="paragraph">
                <h1 id="heading-paragraph">Who am I?</h1>
                <p id="intro-paragraph">
                    I am 20 year old. I was born and raised in Bangkok. My childhood involves a lot of travelling between Australia 
                    and Thailand. A fun fact about me is that I've moved to 8 different schools before coming to university. My hobbies 
                    incluce playing guitar and competing in soccer against my two golden retrievers.{'\n\n'}

                    Last year I self taught myself HTML, CSS, and Javascript and absolutely loved it. I enjoy spending my free time coming up
                    with new ideas and what are some potential products I can make.
                </p>
                <button className="cv-box">
                    <AiOutlineFileDone size={20} color="white"/>
                    <p id="button-txt" href={cv}>View CV</p>
                </button>
            </div>
        </div>
        <style jsx>{`
        #about-container {
            display: flex;
            align-items: center;
            flex-direction: row;
            justify-content: center;
            border: 1px solid black;
          }

        #paragraph {
            height: 60%;
            width: 40%;
            display: flex;
            justify-content: center;
            flex-direction: column;
            padding-left: 10px;
            background-color: white;
            /* box-shadow: 0 3px 10px rgb(0 0 0 / 0.2); */
            border-radius: 30px;
        }
        #photo {
            height: 60%;
            width: 40%;
            display: flex;
            justify-content: center;
            align-items: center;
        }

        #intro-paragraph {
            font-size: 18px;
            margin-bottom: 20px;
            line-height: 30px;
        }

        #caption-paragraph {
            font-size: 20px;
            margin-bottom: 20px;
        }

        #heading-paragraph {
            font-size: 50px;
            font-weight: 600;
            margin-bottom: 10px;
        }

        #cv-box {
            height: 80px;
            width: 150px;
            background-color: #ed755a;
            box-shadow: 0 3px 10px rgb(0 0 0 / 0.2);
            border-radius: 10px;
            display: flex;
            align-items: center;
            padding-left: 10px;
            padding-right: 20px;
            justify-content: space-between;
            outline: none;
            border: none;
        }

        #prom-photo {
            height: 300px;
        }
        `}</style>
    </>
}
