import React from 'react';
import '../styling/AboutMe.css'
import ImageUploader from 'react-images-upload';
import graduation from "./drawings/graduation.png";

const AboutMe = () => {

    const facts = [[1, "firstfact"], [2, "secondfact"], [3,"thirdfact"]];
    let val = 0;
    let currentFactId = facts[val];

    function changeFact() {

        const currentFact = document.querySelectorAll(".facts");
        for (let i = 0; i < currentFact.length; i ++){

            if (currentFact[i].id == currentFactId[1]){
                console.log(currentFactId[1]);
                val += 1

                if (val > currentFact.length - 1) {
                    val = 0
                }
                currentFactId = facts[val];
                break;
                // if (update) {
                //     currentFact.style.display = "flex";
                //     return;
                // }
            }
        }
        // changeFact(true);
    }

    return (
        <div>
            <link rel="stylesheet" href="AboutMe.css"/>
            <div class="AboutMePage">

                <div class="Information">
                    <div class="MyInfo" id="fact"> 
                        <div class="facts" id="firstfact" style={ { display:"flex", backgroundColor: "green"}}></div>
                        <div class="facts" id="secondfact" style={ { display: "none", backgroundColor: "red"}}></div>
                        <div class="facts" id="thirdfact" style={ { display:"none", backgroundColor: "yellow"}}></div>                    
                    </div>

                    <div class="MyInfo" id="hobby">My Hobbies</div>
                    <div class="MyInfo" id="interest">My Interest</div>
                </div>
                <div class="Information">
                    <div class="buttons">
                        <button id="factButton" onClick={changeFact}></button>
                        <button></button>
                        <button></button>
                    </div>
                </div>

            </div>

        </div>
    )

};

export default AboutMe;
