import React, {useState} from 'react';
import  placeholder  from './placeholder.jpeg'
import bamboo  from './bamboo.png'
import personal  from './personal.png'
import pouch  from './Pouch.png'
import quokka  from './Quokka.png'
import panda from './panda.png'

export default function Projects() {

    return <>
        <div className="projects-container">
            <div className='project-header'>
                <h1 className="header-project">Projects</h1>
            </div>
            <div className="box" id='quokka-box' onClick={() => console.log('here')}>
                <div className='img-box'>
                    <img id='quokka' src={quokka}></img>
                </div>
                <div className='txt-box'>
                    <h1 className='prj-title'>Pouch</h1>
                    <p>A React Native app made to store loyalty points on mobile phone.</p>
                    <div className='language-cont'>
                        <div className='language-box'>React Native</div>
                        <div className='language-box'>Firebase</div>
                        <div className='language-box'>React</div>
                        <div className='language-box'>HTML</div>
                        <div className='language-box'>CSS</div>
                        <div className='language-box'>JS</div>
                    </div>
                </div>
            </div>
            <div className="box" id='auth-box' onClick={() => console.log('here')}>
                <div className='txt-box' id='prj-two'>
                    <h1 className='prj-title'>Web App Authentication</h1>
                    <p>Constructed a login and a sign up page. Design the flow of user authentication, email verification, password reset, and integrated with AWS Cognito.</p>
                    <div className='language-cont'>
                        <div className='language-box'>React</div>
                        <div className='language-box'>Next.js</div>
                        <div className='language-box'>AWS Cognito</div>
                        <div className='language-box'>HTML</div>
                        <div className='language-box'>CSS</div>
                        <div className='language-box'>JS</div>
                    </div>
                </div>
                <div className='img-box'>
                </div>
            </div>

            <div className="box" style={{backgroundColor: '#FFCC66'}} onClick={() => console.log('here')}>
                <div className='img-box'>
                <img id='quokka' src={panda}></img>

                </div>
                <div className='txt-box'>
                    <h1 className='prj-title'>Bamboo</h1>
                    <p>A productivity tracker to make to-do list, earn points upon completing tasks, add friends, view friends tasks and send challenges</p>
                    <div className='language-cont'>
                        <div className='language-box'>React Native</div>
                        <div className='language-box'>Firebase</div>
                        <div className='language-box'>React</div>
                        <div className='language-box'>HTML</div>
                        <div className='language-box'>CSS</div>
                        <div className='language-box'>JS</div>
                    </div>
                </div>
            </div>

            {/* <div className="box">
                    <img className="prjImg" src={bamboo}></img>
                <div id="bottom">
                    <div id="prj-title">Bamboo</div>
                    <div id="prj-caption">Hackiethon 2021 | JS, HTML, CSS, React</div>
                    <div id="prj-des"> A productivity tracker to make to-do list, earn points upon completing tasks, add friends, view friends tasks and send challenges</div>
                <div id="git-button">
                <a href="https://github.com/IdeaVanicha/hackiethon-2021" >
                    <p id="button-txt">Git Hub</p>
                </a>
                <AiFillGithub size={20} color="white"/>

                </div>
                </div>
            </div> */}

        </div>
        <style jsx>{`
        a{
            margin: 0;
            text-decoration: none;
        }

        .projects-container {
            display: flex;
            flex-direction: column;
            justify-content: center;
            align-items: center;
            padding: 100px;
            padding-top: 30px;
          }

          .header-project {
              font-size: 48px;
              font-weight: 600;            
          }


        .box {
            height: 250px;
            width: 100%;
            border: 1px solid black;
            display: flex;
            flex-direction: row;
            overflow: hidden;
            margin-top: 20px;
        }

        .box:hover {
            cursor: pointer;
            transform: translateY(-5px);
        }

        .prjImg {
            object-fit: cover;
            height: 100%;
        }

        #prj-title {
            height: 20%;
            width: 100%;
            font-size: 30px;
            font-weight: 600;
        }

        #prj-caption {
            height: 15%;
            width: 100%;
            color: grey;
            font-size: 14px;
            font-weight: 500;
        }

        #prj-des {
            height: 30%;
            width: 100%;
            font-size: 14px;
            font-weight: 500;
        }

        #git-button {
            height: 40px;
            width: 150px;
            background-color: #ed755a;
            border-radius: 10px;
            display: flex;
            align-items: center;
            padding-left: 10px;
            padding-right: 20px;
            justify-content: space-between;
            margin-top: 10px;
            flex-direction: row;
        }
        #quokka {
            height: auto;
            width: 100%;
            margin-left: 90px;
        }

        #quokka-box {
            background-color: #FF6A6B;
        }

        #auth-box {
            background-color: #151619;
            color: white;
        }

        .img-box {
            flex: 1;
        }

        .txt-box {
            flex: 3;
            display: flex;
            justify-content: flex-end;
            align-items: flex-end;
            padding: 50px;
            flex-direction: column;
        }
        #prj-two {
            justify-content: flex-start;
            align-items: flex-start;  
            flex: 2;
        }
        .language-box {
            display: flex;
            height: 40px;
            border: 1px solid white;
            border-radius: 10px;
            padding: 2px;
            padding-left: 10px;
            padding-right: 10px;
            justify-content: center;
            align-items: center;
            margin-right: 7px;
            color: white;
        }

        .language-box:hover {
            border: 1px solid #FFC0CB;
            color: #FFC0CB;
            transition: all 0.2s linear;
        }

        .language-cont {
            // border: 1px solid black;
            display: flex;
            flex-wrap: wrap;
            margin-top: 20px;
        }
        .prj-title {
            font-size: 44px;
            font-weight: 600;
        }
        .project-header {
            width: 100%;
        }

        `}

        </style>
    </>
}
