import React from 'react';
import '../styling/AboutMe.css'
import ImageUploader from 'react-images-upload';
import graduation from "./drawings/graduation.png";

const AboutMe = () => {

    const facts = [[1, "firstfact"], [2, "secondfact"], [3,"thirdfact"]];
    let val = 0;
    let currentFactId = facts[val];
    let update = false;

    function changeFact() {

        const currentFact = document.querySelectorAll(".facts");
        for (let i = 0; i < currentFact.length; i ++){

            if (currentFact[i].id == currentFactId[1]){

                if (!update) {
                    val += 1

                    if (val > currentFact.length - 1) {
                        val = 0;
                    }
                    currentFactId = facts[val];
                    update = true;
                    currentFact[i].style.display = "none";

                } else {
                    currentFact[i].style.display = "flex";
                    console.log(currentFact[i].style)
                    currentFact[i].style.animationName = "fading";
                    currentFact[i].style.animationName = "fading";
                    update = false;
                    return;
                }
            }
        }
        changeFact();
    }
    return (
        <div>
            <link rel="stylesheet" href="AboutMe.css"/>
            <div class="AboutMePage">

                <div class="Information">
                    <div class="MyInfo" id="fact"> 
                        <div class="facts" id="firstfact" style={ { display:"flex", backgroundColor: "green"}}>
                            <p>Yes my name is Idea. Like I-D-E-A.. Long story short,  my dad wants me to always think 
                                about my actions. And so, he saw this baby girls and went… I will name her Idea!</p>
                        </div>
                        <div class="facts" id="secondfact" style={ { display: "none", backgroundColor: "red"}}>
                            <p>
                            2. My life involves a lot of traveling. I’ve been to 8 different schools before coming to university. 
                            Spending most of my childhood traveling between Australia and Thailand opened up my eyes to different 
                            cultures.
                            </p>
                        </div>
                        <div class="facts" id="thirdfact" style={ { display:"none", backgroundColor: "yellow"}}>
                            <p> 3. Sunflower is my favourite flower! Why? Because they always the sun, just like what I always 
                                tell myself, “Always be optimistic!”.
                            </p>
                        </div>                    
                    </div>

                    <div class="MyInfo" id="hobby">My Hobbies</div>
                    <div class="MyInfo" id="interest">My Interest</div>
                </div>
                <div class="Information">
                    <div class="buttons">
                        <button id="factButton" onClick={changeFact}></button>
                        <button></button>
                        <button></button>
                    </div>
                </div>

            </div>

        </div>
    )

};

export default AboutMe;
