import React from 'react';
import '../styling/Experience.css'
import Sunflower from "./drawings/sunflower.jpg";
import Consulting from './Consulting.js';
import bamboo from "./drawings/bamboo.png";


const Experience = () => {


    let firstClick = false;

    function displayDes() {

        if (!firstClick){
            document.querySelector("#descriptionPro").style.display = "flex";
            document.getElementById("projectImg").style.display = "none";
            firstClick = true;
        } else {
            document.querySelector("#descriptionPro").style.display = "none";
            document.getElementById("projectImg").style.display = "flex";
            firstClick = false;
        }

    }


    return (
        <div>
            <p class="expStick" id="exp">E X P E R I E N C E S</p>
            <div class="Experience">
            <style href="Experience.css"></style>
            <div class="PjTable">
                <div id="PjTitleColumn">
                    <p id="ProjectTitle"> P</p>
                    <p id="ProjectTitle"> R</p>
                    <p id="ProjectTitle"> O</p>
                    <p id="ProjectTitle"> J</p>
                    <p id="ProjectTitle"> E</p>
                    <p id="ProjectTitle"> C</p>
                    <p id="ProjectTitle"> T</p>
                    <p id="ProjectTitle"> S</p>
                </div>
                <div id="PjColumn">
                    <div id="project 1" class="box">
                        <table>
                            <div>
                                <tr id="projectImg"><img id="bambooImg" src={bamboo}></img></tr>
                                <tr id="descriptionPro">
                                    <p>The theme of 2021 Hackiethon was working from home. My team and I designed 
                                "Bamboo" which is productivity application where users can make their own to-do list, earn points upon
                                completing tasks, view their friends tasks and send challenges to their friends. I was involved in building the user authentication system, connecting our application to the backend and 
                                handled some css and javascript.</p>

                                </tr>
                            </div>

                            <tr id="projectDes">
                                <button class="projectButtons">GitHub</button>
                                <button class="projectButtons" onClick={ displayDes }>Description</button>
                            </tr>

                        </table>
                    </div>

                </div>
            </div>
        </div>
        <Consulting></Consulting>
        </div>

    )

};

export default Experience;
